var app=angular.module("app",[]);app.controller("todoCtrl",function(t,o){t.todoList=o.get(),t.add=function(){t.todoList.push({title:"",done:!1})},t.save=function(e){"13"==e.keyCode&&o.put(t.todoList)}}),app.factory("storage",function(){var t="myToDo";return{get:function(){return JSON.parse(localStorage.getItem(t)||'[{ "title": "", "done": false }]')},put:function(o){localStorage.setItem(t,JSON.stringify(o))}}});